using System;

namespace Advent2016
{

    public class Day2 : IExecutable
    {

        public void Execute()
        {

            string code1 = "";
            string code2 = "";
            string code3 = "";
            string code4 = "";
            string code5 = "";

            KeyPad pad1 = new KeyPad();
            KeyPad pad2 = new KeyPad();
            KeyPad pad3 = new KeyPad();
            KeyPad pad4 = new KeyPad();
            KeyPad pad5 = new KeyPad();
            KeyPad13 pad6= new KeyPad13();
            KeyPad13 pad7 = new KeyPad13();
            KeyPad13 pad8 = new KeyPad13();
            KeyPad13 pad9 = new KeyPad13();
            KeyPad13 pad10 = new KeyPad13();

            foreach(char command in code1)
            {
                interpretCommand(command, pad1);
            }
            foreach(char command in code2)
            {
                interpretCommand(command, pad2);
            }
            foreach(char command in code3)
            {
                interpretCommand(command, pad3);
            }
            foreach(char command in code4)
            {
                interpretCommand(command, pad4);
            }
            foreach(char command in code5)
            {
                interpretCommand(command, pad5);
            }

            foreach(char command in code1)
            {
                interpretCommand(command, pad6);
            }
            foreach(char command in code2)
            {
                interpretCommand(command, pad7);
            }
            foreach(char command in code3)
            {
                interpretCommand(command, pad8);
            }
            foreach(char command in code4)
            {
                interpretCommand(command, pad9);
            }
            foreach(char command in code5)
            {
                interpretCommand(command, pad10);
            }
            

            Console.WriteLine("Challenge 1 Code is: " + pad1.GetNumber() + pad2.GetNumber() + pad3.GetNumber() + pad4.GetNumber() + pad5.GetNumber());
            Console.WriteLine("Challenge 2 Code is: " + pad6.GetFormattedNumber() + pad7.GetFormattedNumber() + pad8.GetFormattedNumber() + pad9.GetFormattedNumber() + pad10.GetFormattedNumber());

        }

        public void interpretCommand(char command, IKeyPad pad)
        {
            if(command == 'U')
            {
                pad.MoveUp();
            }
            if(command == 'R')
            {
                pad.MoveRight();
            }
            if(command == 'D')
            {
                pad.MoveDown();
            }
            if(command == 'L')
            {
                pad.MoveLeft();
            }
        }

    }

}